import axios from 'axios';
import React, { useEffect, useState } from 'react'

interface IUser {
	user_id: number,
	username: string,
	email: string,
	sold: number,
}

function initialState(): IUser[] {
	return []
}

function Profile(user: IUser) {
	const [details, setDetails] = useState(<></>);
	const [shown, setShown] = useState(false);

	const ShowDetails = () => {
		if (!shown) {
			setShown(true);

			setDetails(
				<>
				<li className='user-profile-details' key={2}>{user.email}</li>
				<li className='user-profile-details' key={3}>sold: {user.sold}</li>
				</>
			);

		} else {
			setShown(false);
			setDetails(<></>);
		}
	}

	return (
		<li>
			<ul className='user-profile'>
				<li><a className='user-profile-username' onClick={ShowDetails}>{user.username}</a></li>
				<button></button>
				{details}
			</ul>
		</li>
	)
}

export const Users = () =>{

	const [users, setUsers] = useState(() => initialState());

	useEffect(() =>  {
		fetch('http://localhost:8080/users/')
			.then((data) => data.json())
			.then((json: IUser[]) => {
				// const data = JSON.stringify(json);
				setUsers(json);
				console.log("data received: ", json);
			})
	}, [])

	return (
		<>
			<ul className='user-list'>
				{/* <div className='user-list-header'>
					<span><strong>username</strong></span>
					<span>email</span>
					<span>sold</span>
				</div> */}
				{users.map((user) => <Profile {...user} />)}
			</ul>
		</>
	)
}
