import React, { useEffect, useState } from 'react';
import { Users } from './components/user_list'
import './App.css';
import { CreateUser } from './components/create_user';

enum UserMode {
  ShowUsers = "show",
  CreateUsers = "create",
}

function App() {
  const [actualMode, setMode] = useState(UserMode.ShowUsers)
  const [content, setContent] = useState(<Users />)

  useEffect(() => {
    console.log("changing render...")
    if (actualMode === UserMode.ShowUsers) {
      setContent(<Users />);
    } else if (actualMode === UserMode.CreateUsers) {
      setContent(<CreateUser />);
    }
  }, [actualMode])

  function DefaultMode() {
    console.log("setting mode to default");
    setMode(UserMode.ShowUsers);
  }

  function CreateUserMode() {
    console.log("setting mode to create-user");
    setMode(UserMode.CreateUsers);
  }

  const UnderlineIfSelected = (mode: UserMode) => {
    return (mode === actualMode)? {textDecoration: 'underline #d0c9c8'} : {}
  }

  return (
    <div className="App">
      <ul className='menu-bar'>
        <li key="default">
          <a href='#' onClick={DefaultMode} style={UnderlineIfSelected(UserMode.ShowUsers)}>Show</a>
        </li>
        <li key="create">
          <a href='#' onClick={CreateUserMode} style={UnderlineIfSelected(UserMode.CreateUsers)}>Create</a>
        </li>
      </ul>
      {content}
    </div>
  );
}

export default App;
